    this._baseUrl = baseUrl;
		// this._from = from;
		// this._to = to;
  }

  _checkResponse(res) {
    if (res.ok) {
      return res.json();
    } else {
      return Promise.reject(res.statusText);
    }
  }

  getDefinition(word) {
    // return fetch(`${this._baseUrl}?apiKey=${API_KEY}&q=${keyword}&from=${from}&to=${to}&pageSize=100`)
    // return fetch(this._baseUrl + word)
    // .then((res) => this._checkResponse(res))

    return fetch('https://wordsapiv1.p.rapidapi.com/words/?letterPattern=%5Ea.%7B4%7D%24&pronunciationpattern=.*%C3%A6m%24&limit=100&page=1&hasDetails=hasDetails')
    .then(response => response.json())
    .then(response => console.log(response))
    .catch(err => console.error(err));
  }

}

