import React from 'react';

import './App.css';
import Header from '../Header/Header';
import Main from '../Main/Main';
import Definitions from '../Definitions/Definitions';

import wordsApi from '../../api/WordsApi';

function App() {

  const definitionArray = [];
  const [ definitions, setDefinitions ] = React.useState([]);

  function handleSubmit(word) {
    console.log('word:', word);
    wordsApi.getDefinitions(word)
    .then((res) => {
      console.log('res from api:', res.definitions);
      // setDefinitions(res.definitions[0].definition);
      res.definitions.map(item => {
        definitionArray.push(item.definition);
      })
      // console.log('New Array:', definitionArray);
      // if ()
      // setDefinitions(definitionArray);
      setDefinitions([definitionArray]);
    })
    .catch(err => console.error(err));
  }

  return (
    <div className="page">

      <Header></Header>
      <Main
        onSubmit={handleSubmit}
      ></Main>
      <Definitions
        definitions={definitions}
      ></Definitions>

    </div>
  );
}

export default App;


